import { STORE_DEFAULT_NAME } from '../constants';
import getStoreData from './getBrowserStoreData';
import { MiddleWare, GlobalState } from '../types';

class StoreFactory {
  public storageType: Storage;
  public state: GlobalState = {};
  public middleWares: MiddleWare[] = [];

  constructor(public name = STORE_DEFAULT_NAME) {
    this.storageType =
      typeof sessionStorage !== 'undefined'
        ? window.sessionStorage
        : ({} as Storage);
  }

  updateStore(defaultValues: GlobalState) {
    this.state = getStoreData(this.storageType, this.name) || defaultValues;
  }

  updateMiddleWares(middleWares: MiddleWare[]) {
    return (this.middleWares = middleWares);
  }
}

export default new StoreFactory();
export const STORE_DEFAULT_NAME = '__LSM__';
export const STORE_ACTION_NAME = '__LSM_NAME__';
export default (storageType: Storage, storeName: string) => {
  try {
    return JSON.parse(storageType.getItem(storeName) as string);
  } catch {
    return null;
  }
};
import * as React from 'react';
import storeFactory from './logic/storeFactory';
import { StateMachineContextValue } from './types';

const StateMachineContext = React.createContext<StateMachineContextValue>(undefined as any);

export const StateMachineProvider: React.FC = ({ children }) => {
  const [state, setState] = React.useState(storeFactory.state);

  return (
    <StateMachineContext.Provider
      value={{ state, setState }}
    >
      { children }
    </StateMachineContext.Provider>
  );
}

export function useStateMachineContext() {
  const value = React.useContext<StateMachineContextValue>(StateMachineContext);

  if (process.env.NODE_ENV !== 'production') {
    if (!value) {
      console.error(`StateMachine context is undefined, please verify you are calling useStateMachine() as child of a <StateMachineProvider> component.`)
    }
  }

  return value;
}
import * as React from 'react';
import { useStateMachineContext } from './StateMachineContext';
import storeFactory from './logic/storeFactory';
import { setUpDevTools } from './logic/devTool';
import {
  StateMachineOptions,
  GlobalState,
  AnyCallback,
  AnyActions,
  ActionsOutput,
} from './types';
import { STORE_ACTION_NAME, STORE_DEFAULT_NAME } from './constants';

export function createStore(
  defaultState: GlobalState,
  options: StateMachineOptions = {
    name: STORE_DEFAULT_NAME,
    middleWares: [],
  },
) {
  options.name && (storeFactory.name = options.name);
  options.storageType && (storeFactory.storageType = options.storageType);
  storeFactory.updateMiddleWares(options.middleWares);

  if (process.env.NODE_ENV !== 'production') {
    setUpDevTools(
      storeFactory.storageType,
      storeFactory.name,
      storeFactory.state,
    );
  }

  storeFactory.updateStore(defaultState);
}

function actionTemplate<TCallback extends AnyCallback>(
  setState: React.Dispatch<React.SetStateAction<GlobalState>>,
  callback: TCallback,
) {
  return (payload: Parameters<TCallback>[1]) => {
    if (process.env.NODE_ENV !== 'production') {
      window[STORE_ACTION_NAME] = callback ? callback.name : '';
    }

    storeFactory.state = callback(storeFactory.state, payload);

    storeFactory.storageType.setItem(
      storeFactory.name,
      JSON.stringify(storeFactory.state),
    );

    if (storeFactory.middleWares.length) {
      storeFactory.state = storeFactory.middleWares.reduce(
        (currentValue, currentFunction) =>
          currentFunction(currentValue) || currentValue,
        storeFactory.state,
      );
    }

    setState(storeFactory.state);
  };
}

export function useStateMachine<TCallback extends AnyCallback, TActions extends AnyActions<TCallback>>(
  actions?: TActions,
): {
  actions: ActionsOutput<TCallback, TActions>;
  state: GlobalState;
} {
  const { state, setState } = useStateMachineContext();

  return React.useMemo(
    () => ({
      actions: actions
        ? Object.entries(actions).reduce(
            (previous, [key, callback]) =>
              Object.assign({}, previous, {
                [key]: actionTemplate(setState, callback),
              }),
            {},
          )
        : ({} as any),
      state,
    }),
    [state, setState, actions],
  );
}
import { GlobalState } from "../types";

export function setUpDevTools(
  storageType: Storage,
  name: string,
  state: GlobalState,
) {
  if (typeof window === 'undefined') return;

  window.__LSM_NAME__ = name;

  window.__LSM_DEBUG__ = (value: string) =>
    storageType.setItem('___LSM_DEBUG__', value);

  window.__LSM_RESET__ = () => storageType.clear();

  window.__LSM_GET_STORE__ = () => storageType.getItem(name);

  window.__LSM_SAVE_TO__ = (name: any) =>
    window.localStorage.setItem(name, JSON.stringify(state));

  window.__LSM_LOAD__ = ({
    storeName,
    data,
  }: {
    storeName: string;
    data?: string;
  }) =>
    storageType.setItem(
      name || '___LSM_DEBUG__',
      data || window.localStorage.getItem(storeName) || '',
    );
}
